            rgba(179, 184, 233, 1) 0px,
            rgba(116, 127, 230, 1) 45px,
            rgba(218, 218, 218, 1) 45px,
            rgba(255, 255, 255, 1) 63%,
            rgba(218, 218, 218, 1) 100%);
    border-radius: 10px;
    box-shadow: 0px 0px 5px black;
    /* display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center; */
    height: fit-content;
    margin: 20px;
    overflow-x: hidden;
    padding: 10px;
