import { createRouter, createWebHistory } from 'vue-router';
import {
  getAuth,
  signInWithPopup,
  GoogleAuthProvider,
} from "firebase/auth";

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: [
    {
      path: '/',
      name: 'home',
      component: () => import('../views/HomeView.vue'),
    },
    {
      path: '/list',
      name: 'list',
      component: () => import('../views/ListView.vue'),
      meta: {
        requiresAuth: true,
      }
    },
    {
      path: '/groupList',
      name: 'groupList',
      component: () => import('../views/GroupListView.vue'),
      meta: {
        requiresAuth: true,
      }
    },
    {
      path: '/groups',
      name: 'groups',
      component: () => import('../views/GroupsView.vue'),
      meta: {
        requiresAuth: true,
      }
    },
    {
      path: '/groupPage',
      name: 'groupPage',
      component: () => import('../views/GroupInfoView.vue'),
      meta: {
        requiresAuth: true,
      }
    },
    {
      path: '/birthdays',
      name: 'birthdays',
      component: () => import('../views/Birthdays.vue'),
      meta: {
        requiresAuth: true,
      }
    },
    {
      path: '/spreadsheet',
      name: 'spreadsheet',
      component: () => import('../views/Spreadsheet.vue'),
      meta: {
        requiresAuth: true,
      }
    }
  ]
});

router.beforeEach(async (to) => {
  // routes with `meta: { requiresAuth: true }` will check for
  // the users, others won't
  if (to.meta.requiresAuth) {
    const auth = getAuth();
    console.log("auth", auth);
    const currentUser = auth.currentUser;
    console.log("currentUser", currentUser);
    // if the user is not logged in, redirect to the login page
    if (currentUser === null) {
      const googleAuthProvider = new GoogleAuthProvider();
      signInWithPopup(auth, googleAuthProvider).catch((reason) => {
        console.error("Failed signinRedirect", reason);
        error.value = reason;
      });
      const userDoc = await this.$db.collection("users").doc(currentUser.uid).get();
      if (userDoc.exists) {
        const saveData = userDoc.data();
        if (saveData.groupCategories === undefined) {
          this.$db.collection("users").doc(this.currUser.uid).set(
            {
              "groupCategories": [
                {
                  "catName": "Unsorted",
                  "groups": []
                },
                {
                  "catName": "Ults",
                  "groups": []
                },
                {
                  "catName": "Semis",
                  "groups": []
                },
                {
                  "catName": "Regs",
                  "groups": []
                }
              ]
            }, { merge: true }
          );
        }
      } else {
        const saveData = await this.$db.collection("users").doc(currentUser.uid).set(
          {
            "categories": [
              {
                "catName": "Unsorted",
                "people": []
              },
              {
                "catName": "Ults",
                "people": []
              },
              {
                "catName": "Semis",
                "people": []
              },
              {
                "catName": "Regs",
                "people": []
              }
            ],
            "groupCategories": [
              {
                "catName": "Unsorted",
                "groups": []
              },
              {
                "catName": "Ults",
                "groups": []
              },
              {
                "catName": "Semis",
                "groups": []
              },
              {
                "catName": "Regs",
                "groups": []
              }
            ]
          }
        );
      }
      return {
        query: {
          // we keep the current path in the query so we can
          // redirect to it after login with
          // `router.push(route.query.redirect || '/')`
          redirect: to.fullPath,
        },
      }
    }
  }
})

export default router
