router.beforeEach(async (to) => {
  // routes with `meta: { requiresAuth: true }` will check for
  // the users, others won't
  if (to.meta.requiresAuth) {
    const currentUser = await getCurrentUser()
    // if the user is not logged in, redirect to the login page
    if (!currentUser) {
      const auth = getAuth();
      const currentUser = auth.currentUser;
      this.currUser = currentUser;
      const userDoc = await this.$db.collection("users").doc(currentUser.uid).get();
      if (userDoc.exists) {
        const saveData = userDoc.data();
        this.fireSaveData = saveData;
        if (this.fireSaveData.groupCategories === undefined) {
          this.$db.collection("users").doc(this.currUser.uid).set(
            {
              "groupCategories": [
                {
                  "catName": "Unsorted",
                  "groups": []
                },
                {
                  "catName": "Ults",
                  "groups": []
                },
                {
                  "catName": "Semis",
                  "groups": []
                },
                {
                  "catName": "Regs",
                  "groups": []
                }
              ]
            }, { merge: true }
          );
        }
      } else {
        const saveData = await this.$db.collection("users").doc(currentUser.uid).set(
          {
            "categories": [
              {
                "catName": "Unsorted",
                "people": []
              },
              {
                "catName": "Ults",
                "people": []
              },
              {
                "catName": "Semis",
                "people": []
              },
              {
                "catName": "Regs",
                "people": []
              }
            ],
            "groupCategories": [
              {
                "catName": "Unsorted",
                "groups": []
              },
              {
                "catName": "Ults",
                "groups": []
              },
              {
                "catName": "Semis",
                "groups": []
              },
              {
                "catName": "Regs",
                "groups": []
              }
            ]
          }
        );
      }
      return {
        query: {
          // we keep the current path in the query so we can
          // redirect to it after login with
          // `router.push(route.query.redirect || '/')`
          redirect: to.fullPath,
        },
      }
    }
  }
})

export default router
